import{P as a,S as t}from"./chunks/Screenshot.c5e62441.js";import{d as n,c as i,H as e,Q as o,o as s}from"./chunks/framework.f95d50c3.js";import"./chunks/community-projects.c53947c7.js";const r=o('<p>Find the true size of an npm package.</p><h1 id="why" tabindex="-1">Why? <a class="header-anchor" href="#why" aria-label="Permalink to &quot;Why?&quot;">​</a></h1><h2 id="for-package-users" tabindex="-1"><strong>For Package Users</strong> <a class="header-anchor" href="#for-package-users" aria-label="Permalink to &quot;**For Package Users**&quot;">​</a></h2><h3 id="get-insights" tabindex="-1">Get Insights <a class="header-anchor" href="#get-insights" aria-label="Permalink to &quot;Get Insights&quot;">​</a></h3><ul><li><strong>Get insights:</strong> Package dependencies can be deceiving—a package with one direct dependency may actually install 100 indirect dependencies. Find out everything getting installed.</li></ul><h3 id="install-cost" tabindex="-1">Install Cost <a class="header-anchor" href="#install-cost" aria-label="Permalink to &quot;Install Cost&quot;">​</a></h3><ul><li><strong>Install cost:</strong> The size of <code>node_modules</code> can easily get out of control. Be mindful of what you&#39;re installing and keep it in check.</li></ul><h3 id="bundle-cost" tabindex="-1">Bundle Cost <a class="header-anchor" href="#bundle-cost" aria-label="Permalink to &quot;Bundle Cost&quot;">​</a></h3><ul><li><strong>Bundle cost:</strong> Importing a large dependency to your web app can easily impact its performance. It can bloat your app, slow-down load time, and increase memory usage. Optimize for ESM packages that are tree-shakeable.</li></ul><h3 id="for-package-authors" tabindex="-1"><strong>For Package Authors</strong> <a class="header-anchor" href="#for-package-authors" aria-label="Permalink to &quot;**For Package Authors**&quot;">​</a></h3><h3 id="install-speed" tabindex="-1">Install Speed <a class="header-anchor" href="#install-speed" aria-label="Permalink to &quot;Install Speed&quot;">​</a></h3><ul><li><strong>Install speed:</strong> This is especially important if your package is used by CLI tools that can be loaded with <a href="https://docs.npmjs.com/cli/v8/commands/npx" target="_blank" rel="noreferrer">npx</a>.</li></ul><h3 id="unideal-environments" tabindex="-1">Unideal Environments <a class="header-anchor" href="#unideal-environments" aria-label="Permalink to &quot;Unideal Environments&quot;">​</a></h3><ul><li><strong>Unideal environments:</strong> Your package may be downloaded with slow internet, on slow devices, with little storage. It may even be downloaded within the browser via WebContainers.</li></ul><h3 id="security" tabindex="-1">Security <a class="header-anchor" href="#security" aria-label="Permalink to &quot;Security&quot;">​</a></h3><ul><li><strong>Security:</strong> Keeping dependencies to a minimum will reduce points of failure: unexpected breaking changes, malicious code, and dependency bloat.</li></ul>',16),m=JSON.parse('{"title":"pkg-size","description":"Find the true size of an npm package.","frontmatter":{"title":"pkg-size","description":"Find the true size of an npm package.","outline":[2,3],"head":[["meta",{"property":"og:title","content":"pkg-size"}],["meta",{"property":"og:image","content":"https://webcontainers.io/img/og/guide-community_inspirations.png"}],["meta",{"name":"twitter:title","content":"pkg-size"}],["meta",{"name":"twitter:description","content":"Find the true size of an npm package."}]]},"headers":[],"relativePath":"community-projects/pkg-size.md","filePath":"community-projects/pkg-size.md","lastUpdated":1718304077000}'),l={name:"community-projects/pkg-size.md"},k=n({...l,setup(c){return(d,h)=>(s(),i("div",null,[e(a,{title:"pkg-size",category:"tool"}),e(t,{src:"/img/community/pkg-size.png",alt:"pkg-size",href:"https://pkg-size.dev/"}),r,e(t,{src:"/img/community/pkg-size.png",alt:"pkg-size",href:"https://pkg-size.dev/"})]))}});export{m as __pageData,k as default};
